var express = require('express');
var router = express.Router();
const path = require('path');

const index = path.join(__dirname, "../..", "frontend/build/index.html");

// 전체 유저 정보
router.get('*', function (req, res, next) {
    res.sendFile(index);
});


module.exports = router;

